public boolean colliding(ball ball)
{
    float xd = position.getx()   ball.position.getx();
    float yd = position.gety()   ball.position.gety();

    float sumradius = getradius() + ball.getradius();
    float sqrradius = sumradius * sumradius;

    float distsqr = (xd * xd) + (yd * yd);

    if (distsqr <= sqrradius)
    {
        return true;
    }

    return false;
}

public void resolvecollision(ball ball)
{
       get the mtd
    vectord delta = (position.subtract(ball.position));
    float d = delta.getlength();
       minimum translation distance to push balls apart after intersecting
    vectord mtd = delta.multiply(((getradius() + ball.getradius()) d) d); 


       resolve intersection   
       inverse mass quantities
    float im =    getmass(); 
    float im =    ball.getmass();

       push pull them apart based off their mass
    position = position.add(mtd.multiply(im   (im + im)));
    ball.position = ball.position.subtract(mtd.multiply(im   (im + im)));

       impact speed
    vectord v = (this.velocity.subtract(ball.velocity));
    float vn = v.dot(mtd.normalize());

       sphere intersecting but moving away from each other already
    if (vn > .f) return;

       collision impulse
    float i = ( (.f + constants.restitution) * vn)   (im + im);
    vectord impulse = mtd.normalize().multiply(i);

       change in momentum
    this.velocity = this.velocity.add(impulse.multiply(im));
    ball.velocity = ball.velocity.subtract(impulse.multiply(im));

}

